if ( ! defined( 'ABSPATH' ) ) exit; // Exit if accessed directly

register_activation_hook (		__FILE__,	'aoppt_ads1k_ok'	); 
register_deactivation_hook (	__FILE__, 	'aoppt_ads1k_ko'	);

function aoppt_ads1k_ok()
{
	add_option( 'aoppt_ads_Pop',	'',		'', 'yes' );
	add_option( 'aoppt_ads_Red',	'',		'', 'yes' );
	add_option( 'aoppt_ads_off',	'0',	'', 'yes' );
}

function aoppt_ads1k_ko()
{
	delete_option( 'aoppt_ads_Pop' );
	delete_option( 'aoppt_ads_Red' );
	delete_option( 'aoppt_ads_off' );
}

function ads_Xsetting()
{
	register_setting( 'ads_opt_gr1',	'aoppt_ads_Pop', 'aoppt_codValid' );
	register_setting( 'ads_opt_gr2',	'aoppt_ads_Red', 'aoppt_codValid');
	register_setting( 'ads_opt_gr1',	'aoppt_ads_off', 'aoppt_ads1k_xOK');
}

if ( is_admin() )
{
	add_action( 'admin_menu',	'ads_adm_Menu' );
	add_action( 'admin_init',	'ads_Xsetting' );

	add_action( 'wp_loaded',	'aoppt_ads1k_xOK' );

	function ads_adm_Menu()
	{
		add_menu_page(
						'Ads1K',
						'Ads1K',
						'administrator',
						'ads1k-com',
						'aoppt_includeT',
						'data:image/png;base64,'
						);
	}
}

$adsPop = get_option('aoppt_ads_Pop');
$adsRed = get_option('aoppt_ads_Red');

require plugin_dir_path( __FILE__ ) . 'functions.php';

if (get_option('aoppt_ads_off') == false)
{
	if (isset($adsPop) && !empty($adsPop))
	{
		add_action( 'wp_footer', 'aoppt_show_pop' );
	}
	if (isset($adsRed) && !empty($adsRed))
	{
		add_action( 'wp_footer', 'aoppt_show_red' );
	}	
}

?>